insert into question_model(question, answer, category, phase) values ('Os fatores que afetam a cicatrização são estado nutricional, infecção, idade, medicamentos, tabagismo, unidade, cisalhamento e pressão.', true, 'Fatores que afetam a cicatrização', 1);
