insert into question_model(question, answer, category, phase) values ('As feridas podem ser classificadas quanto ao agente causal como incisivas ou cirúrgicas e contusas, lacerantes e perfurantes.', true, 'Classificação das feridas', 1);
insert into question_model(question, answer, category, phase) values ('As características clínicas, especificando a sua localização, seu tamanho, característica da pele, quantidade de exsudato, odor, tipo de tecido presente na lesão.', false, 'Classificação das feridas', 1);
insert into question_model(question, answer, category, phase) values ('Os itens essenciais que devem ser avaliados em uma ferida são: tipo de lesão, localização da lesão, caracterização do leito da ferida, tipo e quantidade do exsudato, tamanho, profundidade e localização, características da margem da ferida, condição da pele perilesional, presença de túneis e/ou cavidades.', true, 'Itens essenciais que devem ser avaliados em uma ferida', 1);
insert into question_model(question, answer, category, phase) values ('Os itens essenciais que devem ser avaliados em uma ferida são: tipo de lesão, localização da lesão, caracterização do leito da ferida, tipo do exsudato, tamanho, profundidade e localização, características da margem da ferida.', false, 'Itens essenciais que devem ser avaliados em uma ferida', 1);
insert into question_model(question, answer, category, phase) values ('As características clínicas, especificando a sua localização, seu tamanho, característica da pele, quantidade de exsudato, odor, tipo de tecido presente na lesão, dor e os aspectos da pele peri lesão.', true, 'Itens que devem ser registrados na anotação de enfermagem', 1);
insert into question_model(question, answer, category, phase) values ('As feridas podem ser classificadas quanto ao grau de contaminação das feridas, podendo ser limpas, limpas-contaminadas e contaminadas.', false, 'Itens que devem ser registrados na anotação de enfermagem', 1);
insert into question_model(question, answer, category, phase) values ('Os tipos de exsudatos podem ser seroso, serossanguinolento, sanguinolento, purulento, amarelo, marrom ou verde.', true, 'Tipo do exsudato', 1);
insert into question_model(question, answer, category, phase) values ('Os tipos de exsudatos podem ser serossanguinolento, sanguinolento, purulento, amarelo, marrom ou verde.', false, 'Tipo do exsudato', 1);
insert into question_model(question, answer, category, phase) values ('São condições da pele perilesional que devem ser verificadas ao redor da ferida: maceração, vermelhidão, edema, calor e rubor.', true, 'Condições da pele perilesional', 1);
insert into question_model(question, answer, category, phase) values ('São condições da pele perilesional que devem ser verificadas ao redor da ferida: maceração, vermelhidão, edema e calor.', false, 'Condições da pele perilesional', 1);
insert into question_model(question, answer, category, phase) values ('Para mensuração da largura da ferida, o profissional deve utilizar uma régua limpa e descartável, medir o maior comprimento cefalocaudal (ou seja, de cima para baixo) da ferida, medir a maior largura perpendicular (90° em relação ao comprimento) da ferida. Essas medidas ajudam a determinar a área total da lesão.', true, 'Comprimento e largura da ferida', 1);
insert into question_model(question, answer, category, phase) values ('Para mensuração da largura da ferida, o profissional deve utilizar uma régua limpa e descartável, medir o maior comprimento cefalocaudal (ou seja, de baixo para cima) da ferida, medir a maior largura perpendicular (90° em relação ao comprimento) da ferida. Essas medidas ajudam a determinar a área total da lesão.', false, 'Comprimento e largura da ferida', 1);
insert into question_model(question, answer, category, phase) values ('Na avaliação dos pés dos pacientes, os profissionais devem verificar a pele dos pés em busca de qualquer alteração, como feridas, calosidades, bolhas ou áreas avermelhadas e fungos.', true, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('Na avaliação dos pés dos pacientes não é necessário que o profissional avalie as áreas de maior pressão, como calcanhares e região plantar.', false, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('Na avaliação dos pés dos pacientes, os profissionais devem verificar a presença de deformidades nos pés, como dedos em garra, proeminências de metatarsos ou alterações articulares.', true, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('Na avaliação dos pés em pacientes, os profissionais devem avaliar os pulsos pediosos e tibiais posteriores. A ausência de pulsos pode indicar doença vascular periférica.', true, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('Não é necessário avaliar a sensibilidade dos pés utilizando o teste de monofilamento de 20g (para sensibilidade tátil).', false, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('Não é necessário avaliar a sensibilidade dos pés utilizando o teste de diapasão de 140 Hz (para sensibilidade vibratória).', false, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('Não é necessário avaliar a sensibilidade dos pés utilizando o teste de pino ou palito (para sensibilidade dolorosa).', true, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('Não é necessário avaliar a sensibilidade dos pés utilizando o teste de martelo (para reflexo aquileu).', true, 'Avaliação dos pés em pacientes', 1);
insert into question_model(question, answer, category, phase) values ('A mensuração da profundidade da ferida pode ser obtida introduzindo uma espátula estéril ou seringa de insulina sem agulha no ponto mais profundo da lesão. Na altura da borda da ferida mais próxima ao local onde está inserida a seringa, marca-se o ponto correspondente à altura, na espátula ou seringa.', true, 'Mensuração de ferida', 1);
insert into question_model(question, answer, category, phase) values ('A mensuração da profundidade da ferida pode ser obtida introduzindo uma espátula estéril ou seringa de insulina sem agulha no ponto mais profundo da lesão. Na altura da margem da ferida mais próxima ao local onde está inserida a seringa, marca-se o ponto correspondente à altura, na espátula ou seringa.', false, 'Mensuração de ferida', 1);
insert into question_model(question, answer, category, phase) values ('O tecido de granulação é uma estrutura formada durante o processo de cicatrização de feridas. Ele é composto por células especializadas, como fibroblastos, macrófagos e células endoteliais, além de uma matriz extracelular rica em colágeno.', true, 'Definição de tecido de granulação', 2);
insert into question_model(question, answer, category, phase) values ('O tecido de granulação inviável é caracterizado por apresentar uma coloração avermelhada ou rosada, devido à presença de vasos sanguíneos recém-formados.', false, 'Definição de tecido de granulação', 2);
insert into question_model(question, answer, category, phase) values ('Necrose indica morte celular ou de tecidos no organismo. Sabe-se que o processo de necrose envolve também o processo inflamatório no local. Várias são as causas de necrose, entre elas, diminuição do aporte de sangue ou falta de oxigenação com a presença de toxinas e enzimas que levam à morte celular.', true, 'Definição de tecido necrosado', 2);
insert into question_model(question, answer, category, phase) values ('Necrose indica morte celular ou de tecidos no organismo. Sabe-se que o processo de necrose envolve também o processo inflamatório, fibroblastos, macrófagos e células endoteliais no local. Várias são as causas de necrose, entre elas, diminuição do aporte de sangue ou falta de oxigenação com a presença de toxinas e enzimas que levam à morte celular.', false, 'Definição de tecido necrosado', 2);
